import React, { useState, useEffect } from 'react';
import { getContract } from './useContract';
import { ethers } from 'ethers';

const [campaigns, setCampaigns] = useState([]);
// … your title/description/goal state, plus refresh …


export default function App() {
  const [campaigns, setCampaigns] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [goalEth, setGoalEth] = useState('');
  const [refresh, setRefresh] = useState(0);

  useEffect(() => {
    (async () => {
      const contract = await getContract();
      const count = await contract.getCampaignCount();
      const list = [];
      for (let i = 1; i <= count.toNumber(); i++) {
        const [t, d, g, f, owner, promos, donors] = await contract.getCampaign(i);
        list.push({
          id: i,
          title: t,
          description: d,
          goal: ethers.utils.formatEther(g),
          funds: ethers.utils.formatEther(f),
          owner,
          promotions: promos.toNumber(),
          donors
        });
      }
      setCampaigns(list);
    })();
  }, [refresh]);

  const handleCreate = async () => {
    const contract = await getContract();
    const tx = await contract.createCampaign(
      title,
      description,
      ethers.utils.parseEther(goalEth)
    );
    await tx.wait();
    setTitle('');
    setDescription('');
    setGoalEth('');
    setRefresh(r => r + 1);
  };

  const handleDonate = async (id, amount) => {
    const contract = await getContract();
    const tx = await contract.donate(
      id,
      { value: ethers.utils.parseEther(amount) }
    );
    await tx.wait();
    setRefresh(r => r + 1);
  };

  const handlePromote = async id => {
    const contract = await getContract();
    const tx = await contract.promote(id);
    await tx.wait();
    setRefresh(r => r + 1);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Kickstart DApp</h1>
      <div className="mb-6 p-4 border rounded">
        <h2 className="text-xl mb-2">Create Campaign</h2>
        <input
          className="border p-2 w-full mb-2"
          placeholder="Title"
          value={title}
          onChange={e => setTitle(e.target.value)}
        />
        <textarea
          className="border p-2 w-full mb-2"
          placeholder="Description"
          value={description}
          onChange={e => setDescription(e.target.value)}
        />
        <input
          className="border p-2 w-full mb-2"
          placeholder="Goal (ETH)"
          value={goalEth}
          onChange={e => setGoalEth(e.target.value)}
        />
        <button
          className="bg-blue-500 text-white px-4 py-2 rounded"
          onClick={handleCreate}
        >
          Create
        </button>
      </div>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        {campaigns.map(c => (
          <div key={c.id} className="border p-4 rounded shadow">
            <h3 className="font-semibold mb-1">{c.title}</h3>
            <p className="mb-2">{c.description}</p>
            <p>Goal: {c.goal} ETH</p>
            <p>Funds: {c.funds} ETH</p>
            <p>Promotions: {c.promotions}</p>
            <p>Progress: {((+c.funds / +c.goal) * 100).toFixed(1)}%</p>
            <div className="mt-2">
              <input
                id={`donate-${c.id}`}
                className="border p-1 w-1/2"
                placeholder="ETH"
              />
              <button
                className="ml-2 bg-green-500 text-white px-2 py-1 rounded"
                onClick={() => {
                  const amt = document.getElementById(`donate-${c.id}`).value;
                  handleDonate(c.id, amt);
                }}
              >
                Donate
              </button>
              <button
                className="ml-2 bg-yellow-500 text-white px-2 py-1 rounded"
                onClick={() => handlePromote(c.id)}
              >
                Promote
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Kickstart DApp</h1>

      {/* Create Campaign */}
      <div className="mb-6 p-4 border rounded">
        <h2 className="text-xl mb-2">Create Campaign</h2>
        {/* ... your title/description/goal inputs and Create button ... */}
      </div>

      {/* Running Campaigns */}
      <div className="mb-6 p-4 border rounded">
        <h2 className="text-xl mb-2">Running Campaigns</h2>
        {campaigns.length === 0 ? (
          <p className="text-gray-600">No campaigns yet.</p>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            {campaigns.map(c => (
              <div key={c.id} className="border p-4 rounded shadow">
                <h3 className="font-semibold mb-1">{c.title}</h3>
                <p className="mb-2">{c.description}</p>
                <p>Goal: {c.goal} ETH</p>
                <p>Funds: {c.funds} ETH</p>
                <p>Progress: {((+c.funds / +c.goal) * 100).toFixed(1)}%</p>
                <div className="mt-2">
                  <input
                    id={`donate-${c.id}`}
                    className="border p-1 w-1/2"
                    placeholder="ETH"
                  />
                  <button
                    className="ml-2 bg-green-500 text-white px-2 py-1 rounded"
                    onClick={() => {
                      const amt = document.getElementById(`donate-${c.id}`).value;
                      handleDonate(c.id, amt);
                    }}
                  >
                    Donate
                  </button>
                  <button
                    className="ml-2 bg-yellow-500 text-white px-2 py-1 rounded"
                    onClick={() => handlePromote(c.id)}
                  >
                    Promote
                  </button>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );

}
